import React from 'react';

class Item extends React.Component {
    render() {
        return (
            <li>
                {this.props.name}   
                ${this.props.price}
            </li>
        );
    }
}

class App extends React.Component{
    state = {
        items: [
            {id: 1, name: 'Apple', price: '0.99'},
            {id: 2, name: 'Orange', price: '0.99'},
        ]
    }
    render() {
        return (
            <div>
                <h1>Hello React</h1>
                <ul>
                    {this.state.items.map(i => {
                        return(
                            <Item
                                name={i.name}
                                price={i.price}
                            />    
                        )
                    })}
                </ul>
            </div>
        )
    }
}

export default App;
